import React, {Component} from "react"

class FriendProfileModal extends Component{
    state={
        opacity:0
    }

    getProfile=()=>{
        this.setState({opacity:1})
    }


    render(){
        return(
    <React.Fragment>
    <button onClick={this.getProfile} className='btn btn-primary'>{this.state.opacity === 0 ? "Get Profile" : "Hide Profile"}</button>
    <div style={{opacity:this.state.opacity,width:"200px",height:"200px"}}>
    <h3>{this.props.name}</h3>
    </div>
    </React.Fragment>
        )
    }
}

export default FriendProfileModal
