import React, {Component} from "react"
import Header from "../components/Header.js"
import Navbar from "../components/Navbar.js"
import API from "../utils/API.js"
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider"
import {List, ListItem} from "material-ui/List"



class ScrapeSite extends Component{
    state={
        articles:[],
        images:[],
        counter:0,
        loaded:false,
        site:""
    }

    pickSite=(e,index,value)=>{
        this.setState({site:value})
    }

    componentDidMount=()=>{
        API.scrapeArticles((data)=>{
            //console.log(data)
            this.setState({articles:data.link,
                           images:data.image,
                           loaded:true})
                           console.log(this.state.images)

        })
    }

    nextPic=()=>{
        this.setState({counter:this.state.counter+1})
        if(this.state.counter > this.state.images.length-1){
            this.setState({counter:0})
        }
    }



    render(){
        if(this.state.site === ""){
            return 
            <div className='card text-center'>
            <select name='site'
                    value={this.state.site}
                    onChange={this.pickSite}>
            <option value="climbing">Rock n Ice</option>
            <option value="ny times">NyTimes</option>
            <option value='wayfair'>WayFair</option>
            </select>
            <button onClick={this.siteSelected} className="btn btn-info">Pick a Site</button>
            </div>

        }
        return(
            <MuiThemeProvider>
            <React.Fragment>
            <Header>
            Scrape Site
            </Header>
            <Navbar/>
            <List>
            {this.state.articles.map(article=>(

                <ListItem key={article} primaryText={article}/>


            ))}
            <img onClick={this.nextPic} src={this.state.images[this.state.counter]}/>
            </List>
            </React.Fragment>
            </MuiThemeProvider>
        )
    }
}


export default ScrapeSite;
